import { Component } from '@angular/core';
import { FavoriteChangeEventArg } from './favorites/favorites.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  viewMode = 'map'
}
<ul class="nav">
  <li class="nav-item">
    <a class="nav-link" (click)="viewMode = 'map'">Map view</a>
  </li>
  <li class="nav-item">
    <a class="nav-link" (click)="viewMode = 'list'">List view</a>
  </li>
</ul>

<div>
  <div>Map view content</div>
  <div>List view content</div>
</div>
