import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-like',
  templateUrl: './like.component.html',
  styleUrls: ['./like.component.css']
})
export class LikeComponent {
  @Input('isHeartFill') isHeartFill! : boolean;
  @Input('likeCount') likeCount!: number;

  onClickHeart(){
    this.isHeartFill = !this.isHeartFill;
    console.log(this.isHeartFill);
    this.isHeartFill? this.likeCount++ : this.likeCount--;

  }
}
<i [class.bi-heart]="!isHeartFill" [class.bi-heart-fill]="isHeartFill" 
(click)="onClickHeart()" 
style="font-size: 70px;padding: 200px;color: red;"></i>
<h2>{{likeCount}}</h2>
